mapper.select:
SELECT ad.name AS dimensionName,
       COALESCE(AVG(aa.score), 0) AS avgScore,
       COALESCE(MAX(aa.max_score), 100) AS maxScore
FROM ability_assessments aa
         JOIN ability_dimensions ad ON aa.dimension_id = ad.id
         LEFT JOIN assignments a ON aa.related_id = a.id
WHERE aa.student_id = #{studentId}

      AND a.course_id = #{courseId}


      AND aa.assessed_at BETWEEN #{start} AND #{end}

GROUP BY ad.name

SELECT ad.name AS dimensionName,
       COALESCE(AVG(aa.score), 0) AS avgScore,
       COALESCE(MAX(aa.max_score), 100) AS maxScore
FROM ability_assessments aa
         JOIN ability_dimensions ad ON aa.dimension_id = ad.id
         LEFT JOIN assignments a ON aa.related_id = a.id
WHERE aa.student_id = #{studentId}

      AND a.course_id = #{courseId}


      AND aa.assessed_at BETWEEN #{start} AND #{end}


      AND aa.related_id IN
      #{id}

GROUP BY ad.name

SELECT ad.name AS dimensionName, COALESCE(AVG(aa.score), 0) AS avgScore, COALESCE(MAX(aa.max_score), 100) AS maxScore FROM ability_assessments aa JOIN ability_dimensions ad ON aa.dimension_id = ad.id LEFT JOIN assignments a ON aa.related_id = a.id
JOIN enrollments e ON e.student_id = aa.student_id AND e.course_id = a.course_id
WHERE 1=1 AND a.course_id = #{courseId} AND e.status = 'active'
AND aa.assessed_at BETWEEN #{start} AND #{end}
AND aa.related_id IN #{id}
GROUP BY ad.name

SELECT COALESCE(AVG(g.score), 0) AS avgScore, COALESCE(MAX(g.max_score), 100) AS maxScore FROM grades g JOIN assignments a ON g.assignment_id = a.id
JOIN enrollments e ON e.student_id = g.student_id AND e.course_id = a.course_id
WHERE 1=1 AND a.course_id = #{courseId} AND g.student_id = #{studentId} AND (e.status = 'active' OR e.status = 1) AND g.created_at BETWEEN #{start} AND #{end}
AND g.assignment_id IN #{id}
AND (g.deleted = false OR g.deleted = 0) AND (g.status = 'published' OR g.status = 'PUBLISHED' OR g.status = 1)
