mapper.update:
UPDATE ability_reports
student_id = #{studentId}, report_type = #{reportType}, title = #{title}, overall_score = #{overallScore}, report_period_start = #{reportPeriodStart}, report_period_end = #{reportPeriodEnd}, dimension_scores = #{dimensionScores}, trends_analysis = #{trendsAnalysis}, achievements = #{achievements}, improvement_areas = #{improvementAreas}, recommendations = #{recommendations}, generated_by = #{generatedBy}, is_published = #{isPublished}, published_at = #{publishedAt}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT 
FROM ability_reports r

    r.student_id = #{studentId}
    AND r.report_type = #{reportType}
    AND r.is_published = #{isPublished}

ORDER BY r.report_period_end DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ability_reports r

    r.student_id = #{studentId}
    AND r.report_type = #{reportType}
    AND r.is_published = #{isPublished}

SELECT 
FROM ability_reports r
WHERE r.student_id = #{studentId}
AND r.report_type = #{reportType}
AND r.report_period_start >= #{periodStart}
AND r.report_period_end <= #{periodEnd}
ORDER BY r.report_period_end DESC

SELECT COUNT(1) > 0
FROM ability_reports r
WHERE r.student_id = #{studentId}
  AND r.report_type = #{reportType}
  AND r.report_period_start = #{periodStart}
  AND r.report_period_end = #{periodEnd}

mapper.update:
UPDATE ability_reports
SET is_published = true, published_at = NOW(), updated_at = NOW()
WHERE id = #{id}

UPDATE ability_reports
SET is_published = false, published_at = NULL, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT 
FROM ability_reports r
WHERE r.student_id = #{studentId}
ORDER BY r.overall_score DESC
LIMIT #{limit}
