mapper.select:
SELECT 
FROM ability_reports r

    r.course_id = #{courseId}
    AND r.student_id = #{studentId}

        AND r.assignment_id IN

            #{id}


    AND r.created_at BETWEEN #{start} AND #{end}
    AND r.report_type = 'ai'

ORDER BY r.created_at DESC

mapper.update:
UPDATE ability_reports
student_id = #{studentId}, report_type = #{reportType}, title = #{title}, overall_score = #{overallScore}, report_period_start = #{reportPeriodStart}, report_period_end = #{reportPeriodEnd}, dimension_scores = #{dimensionScores}, trends_analysis = #{trendsAnalysis}, achievements = #{achievements}, improvement_areas = #{improvementAreas}, recommendations = #{recommendations}, ai_history_id = #{aiHistoryId}, generated_by = #{generatedBy}, is_published = #{isPublished}, published_at = #{publishedAt}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT 
FROM ability_reports r
WHERE r.student_id = #{studentId}
AND r.submission_id = #{submissionId}
AND r.assignment_id = #{assignmentId}
AND r.course_id = #{courseId}
AND r.report_type = 'ai'
ORDER BY r.created_at DESC
LIMIT 1

