SELECT ,
       COALESCE(u.nickname, u.username) AS student_name,
       u.student_no AS student_number,
       a.title AS assignment_title
FROM ability_reports r
LEFT JOIN users u ON r.student_id = u.id
LEFT JOIN assignments a ON r.assignment_id = a.id

    1 = 1

        AND (
            u.nickname LIKE CONCAT('%', #{search}, '%')
            OR u.username LIKE CONCAT('%', #{search}, '%')
            OR u.student_no LIKE CONCAT('%', #{search}, '%')
        )

    AND r.student_id = #{studentId}
    AND r.report_type = #{reportType}
    AND r.is_published = #{isPublished}
    AND r.course_id = #{courseId}
    AND r.assignment_id = #{assignmentId}
    AND r.submission_id = #{submissionId}
    AND r.created_at >= #{start}
    AND r.created_at <= #{end}

ORDER BY r.created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ability_reports r
LEFT JOIN users u ON r.student_id = u.id

    1 = 1

        AND (
            u.nickname LIKE CONCAT('%', #{search}, '%')
            OR u.username LIKE CONCAT('%', #{search}, '%')
            OR u.student_no LIKE CONCAT('%', #{search}, '%')
        )

    AND r.student_id = #{studentId}
    AND r.report_type = #{reportType}
    AND r.is_published = #{isPublished}
    AND r.course_id = #{courseId}
    AND r.assignment_id = #{assignmentId}
    AND r.submission_id = #{submissionId}
    AND r.created_at >= #{start}
    AND r.created_at <= #{end}


SELECT



    , COALESCE(u.nickname, u.username) AS student_name,
  u.student_no AS student_number
FROM ability_reports r
LEFT JOIN users u ON r.student_id = u.id
WHERE r.student_id = #{studentId}
  AND r.report_type = #{reportType}
  AND r.report_period_start &gt;= #{periodStart}
  AND r.report_period_end &lt;= #{periodEnd}
ORDER BY r.report_period_end DESC

SELECT COUNT(1) > 0
FROM ability_reports r
WHERE r.student_id = #{studentId}
  AND r.report_type = #{reportType}
  AND r.report_period_start = #{periodStart}
  AND r.report_period_end = #{periodEnd}

mapper.update:
UPDATE ability_reports
SET is_published = true, published_at = NOW(), updated_at = NOW()
WHERE id = #{id}

UPDATE ability_reports
SET is_published = false, published_at = NULL, updated_at = NOW()
WHERE id = #{id}

