mapper.insert:
INSERT INTO assignments (
    course_id, teacher_id, title, description, requirements, max_score,
    due_date, allow_late, max_attempts, file_types, status, submission_count,
    created_at, updated_at, deleted
) VALUES (
    #{courseId}, #{teacherId}, #{title}, #{description}, #{requirements}, #{maxScore},
    #{dueDate}, #{allowLate}, #{maxAttempts}, #{fileTypes}, #{status}, #{submissionCount},
    #{createdAt}, #{updatedAt}, #{deleted}
)

mapper.select:
SELECT FROM assignments a LEFT JOIN courses c ON a.course_id = c.id LEFT JOIN users u ON a.teacher_id = u.id WHERE a.deleted = false
AND a.course_id = #{courseId}
AND a.teacher_id = #{teacherId}
AND a.status = #{status}
AND (a.title LIKE CONCAT('%', #{keyword}, '%') OR a.description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY a.created_at DESC

SELECT COUNT(1) FROM assignments a
WHERE a.deleted = false

    AND a.course_id = #{courseId}


    AND a.teacher_id = #{teacherId}


    AND a.status = #{status}

SELECT FROM assignments a LEFT JOIN courses c ON a.course_id = c.id LEFT JOIN users u ON a.teacher_id = u.id INNER JOIN enrollments e ON a.course_id = e.course_id WHERE a.deleted = false AND a.status = 'published' AND e.student_id = #{studentId}
AND a.course_id = #{courseId}
ORDER BY a.due_date ASC, a.created_at DESC

SELECT status, COUNT(*) as count, AVG(submission_count) as avg_submissions, SUM(submission_count) as total_submissions FROM assignments WHERE deleted = false
AND teacher_id = #{teacherId}
AND course_id = #{courseId}
GROUP BY status
