mapper.select:
SELECT COUNT(1) FROM enrollments e
WHERE 1=1

    AND e.student_id = #{studentId}


    AND e.course_id = #{courseId}


    AND e.status = #{status}

SELECT e.*, COALESCE(u.nickname, u.username) as student_name, c.title as course_name, COALESCE(t.nickname, t.username) as teacher_name FROM enrollments e LEFT JOIN users u ON e.student_id = u.id LEFT JOIN courses c ON e.course_id = c.id LEFT JOIN users t ON c.teacher_id = t.id WHERE 1=1
AND e.student_id = #{studentId}
AND e.course_id = #{courseId}
AND e.status = #{status}
AND (COALESCE(u.nickname, u.username) LIKE CONCAT('%', #{keyword}, '%') OR c.title LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY e.enrolled_at DESC

SELECT student_id FROM enrollments
WHERE course_id = #{courseId} AND student_id IN

    #{studentId}
