mapper.select:
SELECT 
FROM notifications
WHERE user_id = #{recipientId} AND deleted = 0

    AND type = #{type}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE notifications SET is_read = 1, read_at = NOW(), updated_at = NOW() WHERE id IN
#{id}
AND user_id = #{userId} AND deleted = 0

UPDATE notifications
SET deleted = 1, updated_at = NOW()
WHERE id = #{id}
