    student_no, teacher_no, gender, bio, birthday, country, province, city, phone, school, subject, grade,
    email_verified, deleted, created_at, updated_at
) VALUES (
    #{username}, #{email}, #{password}, #{role}, #{avatar}, #{nickname}, #{firstName}, #{lastName},
    #{studentNo}, #{teacherNo}, #{gender}, #{bio}, #{birthday}, #{country}, #{province}, #{city}, #{phone}, #{school}, #{subject}, #{grade},
    #{emailVerified}, #{deleted}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT 
FROM users u
INNER JOIN enrollments e ON u.id = e.student_id
WHERE e.course_id = #{courseId}
  AND u.deleted = false
  AND e.status IN ('active','completed')

  AND (
      u.nickname LIKE #{keyword}
      OR u.username LIKE #{keyword}
      OR u.student_no LIKE #{keyword}
      OR u.teacher_no LIKE #{keyword}
  )



   ORDER BY u.nickname ASC, u.username ASC 
   ORDER BY e.enrolled_at DESC 
   ORDER BY e.enrolled_at DESC

SELECT COUNT(1) FROM users 
WHERE username = #{username} AND deleted = false

    AND id != #{excludeId}

SELECT COUNT(1) FROM users 
WHERE email = #{email} AND deleted = false

    AND id != #{excludeId}

SELECT COUNT(1) FROM users
WHERE student_no = #{studentNo} AND deleted = false

    AND id != #{excludeId}

SELECT COUNT(1) FROM users
WHERE teacher_no = #{teacherNo} AND deleted = false

    AND id != #{excludeId}

SELECT COUNT(1)
FROM enrollments e
WHERE e.course_id = #{courseId}
  AND e.student_id = #{userId}
  AND e.status IN ('active','completed')

SELECT 
FROM users u
INNER JOIN enrollments e ON u.id = e.student_id
WHERE e.course_id = #{courseId}
  AND u.deleted = false
  AND e.status IN ('active','completed')
  AND (
      u.nickname LIKE #{keyword}
      OR u.username LIKE #{keyword}
      OR u.student_no LIKE #{keyword}
      OR u.teacher_no LIKE #{keyword}
  )
ORDER BY e.enrolled_at DESC

SELECT COUNT(1) FROM users 
WHERE deleted = false

    AND role = #{role}
